import React, { useEffect, useState } from "react";
import { getProducts } from "../../../services/itemApi";
import ItemCard from "./ItemCard";
import BestProducts from "./BestProducts"; // BestProducts를 import합니다.

function AllProducts() {
  const [itemList, setItemList] = useState([]);

  useEffect(() => {
    getProducts()
      .then((data) => setItemList(data.list))
      .catch((error) => console.error("Product fetching error:", error));
  }, []);

  return (
    <div className="allProducts">
      {/* BestProducts 컴포넌트를 상단에 렌더링합니다. */}
      <BestProducts />
      <h1 className="sectionTitle">모든 상품</h1>
      <div className="allProductsContainer">
        {itemList.map((item) => (
          <ItemCard key={item.id} item={item} />
        ))}
      </div>
    </div>
  );
}

export default AllProducts;
