import React, { useEffect, useState } from "react";
import { getProducts } from "../../../services/itemApi";
import ItemCard from "./ItemCard";
import BestProducts from "./BestProducts";
import { ReactComponent as SortIcon } from "../../../assets/images/icons/ic_sort.svg";
import { ReactComponent as SearchIcon } from "../../../assets/images/icons/ic_search.svg";
import { Link } from "react-router-dom";

function AllProducts() {
  const [orderBy, setOrderBy] = useState("recent");
  const [page, setPage] = useState(1);
  const [itemList, setItemList] = useState([]);
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);
  const [totalPageNum, setTotalPageNum] = useState();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    getProducts({ page, orderBy })
      .then((data) => {
        setItemList(data.list);
        setTotalPageNum(data.totalCount);
      })
      .catch((error) => console.error("Product fetching error:", error));
  }, [orderBy, page]);

  return (
    <div className="allProducts">
      <div className="bestProductsSection">
        <BestProducts />
      </div>

      <div className="allItemsSection">
        <h1 className="sectionTitle">전체 상품</h1>

        <div className="AllItemsCardSection">
          {itemList?.map((item) => (
            <ItemCard item={item} key={`all-item-${item.id}`} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default AllProducts;
