import React, { useEffect, useState } from "react";
import { getProducts } from "../../../services/itemApi";
import ItemCard from "./ItemCard";

function BestProducts() {
  const [bestProducts, setBestProducts] = useState([]);

  useEffect(() => {
    getProducts()
      .then((data) => {
        // favorite 수를 기준으로 정렬
        const sortedProducts = data.list.sort(
          (a, b) => b.favoriteCount - a.favoriteCount
        );
        // 상위 4개 항목 선택
        const topProducts = sortedProducts.slice(0, 4);
        setBestProducts(topProducts);
      })
      .catch((error) => console.error("Product fetching error:", error));
  }, []);

  return (
    <div className="bestProducts">
      <h1 className="sectionTitle">베스트 상품</h1>
      <div className="bestProductsContainer">
        {bestProducts.map((item) => (
          <ItemCard key={item.id} item={item} />
        ))}
      </div>
    </div>
  );
}

export default BestProducts;
